  Edges <- data.frame("Target" = rep(0, ne), "Source" = rep(0, ne), "Value" = rep(0, ne))
  for (i in 1:ne) {
   Edges$Target[i] <-  data$links[[i]]$target
   Edges$Source[i] <-  data$links[[i]]$source
   Edges$Value[i] <-  data$links[[i]]$value
  }
  Edges %>% head()
  
