   }
   
   # Risky make source target names before making model
   Edges$Target <- factor(labels[(Edges$Target+1)])
   Edges$Source<- factor(labels[(Edges$Source+1)])
   G <- igraph::graph_from_data_frame(Edges, directed = FALSE)
   vertex_attr(G)
   
  # Get the node positions set by the lKamada-Kawai layout for 3D graphs 
   library(tidyverse)
